import { React, useState, useEffect } from "react";
import "./Board.css";
import axios from "axios";

const PostList = (postValue) => {
  let postList = JSON.parse(postValue.postValue);
  //게시글을 자세히 봅니다.
  const getPostDetail = () => {
    let baseUrl = "/post/addPost";
    axios
      .get(baseUrl, {
        // boardid: postid,
      })
      .then((res) => {
        console.log(res);
      });
  };
  const [load, setLoad] = useState(false);
  //게시글 box
  //날짜를 가져오는 함수
  const today = (argDate) => {
    let currentDate = new Date(argDate);
    let year = currentDate.getFullYear();
    let month =
      currentDate.getMonth() + 1 > 9
        ? currentDate.getMonth() + 1
        : "0" + (currentDate.getMonth() + 1);
    let day =
      currentDate.getDate() > 9
        ? currentDate.getDate()
        : "0" + currentDate.getDate();
    let today = year + "년 " + month + "월 " + day + "일";

    console.log("오늘 날짜:", today);

    return today;
  };

  const PostBox = ({ post }) => {
    setLoad(true);

    return (
      <div className="postList_box">
        <div className="post_user_box">
          <div className="post_user_pic"></div>
          <div className="post_user_txt_box">
            <div className="post_user_name">{post.writer}</div>
            <div className="post_date">{today(post.updated_date)}</div>
          </div>
        </div>

        <div className="post_contents">{post.contents}</div>
      </div>
    );
  };
  //Box리턴
  return (
    <div>
      {postList.map((post) => (
        <PostBox post={post} />
      ))}
    </div>
  );
};
export default PostList;
