import React from 'react'
import './Calendar.css';

function mainRender({currentMonth}){
    //현재 월에서 몇주째인지 체크 하는 함수 
    const getWeekOfMonth = (date) =>{
        let DayOfMonth = date.getDate();
        let first = new Date(date.getFullYear() + '/' + (date.getMonth() +1) +'/01');
        let monthFirstDay = first.getDay();

        return Math.ceil((DayOfMonth + monthFirstDay)/7);
        }

    //월의 날짜 리스트를 가져옵니다.
    const makeDateOfMonthList =()=>{
        //현재 날짜가 
        let currentDate = currentMonth !='' ? new Date(currentMonth) : new Date();
        let lastDayOfMonth = new Date(currentDate.getFullYear(), currentDate.getMonth()+1 , 0);
        let monthList = [];
        //월의 마지막날을 가져와서 반복 문을 돌립니다.
        for (let i =1; i<=lastDayOfMonth.getDate(); i++){
            monthList.push(i);
            
        }
        console.log('state 넘어오는지 확인',currentMonth);
        
        return monthList;
        }
        
    const calendarList = () =>{

            return (
                <tr className="cal_list_item">
                <td className="item_0" data-date=""></td>
                <td className="item_1"></td>
                <td className="item_2"></td>
                <td className="item_3"></td>
                <td className="item_4"></td>
                <td className="item_5"></td>
                <td className="item_6"></td>
            </tr>
            )
        }

    return(
        <table className="cal_list" data-calendar-date={currentMonth} data-month-list={currentMonth}>
          <tbody>
              <tr className="cal_head">
                {makeDateOfMonthList}
                <th>일</th>
                <th>월</th>
                <th>화</th>
                <th>수</th>
                <th>목</th>
                <th>금</th>
                <th>토</th>
              </tr>
          </tbody>
        </table>
    )
}
export default mainRender;
