import React,{useState, useEffect} from 'react';
import '../calendar/Calendar.css';
import './Board.css';
import styled from "styled-components";
import axios from 'axios';
// import moment from 'moment';

class BoardAddPop extends React.Component{
    static defaultProps = {
        postHeader :'글등록',
        postConfirmBtn : '확인',
        postTitle :'제목',
    }   
    
    render(){
        
        const closePop =(e)=>{
            this.setState({
                name:e.target
            })
            console.log(e);
            
        }
        const validate= ()=>{
            

        }

        function addPost(){
            
            let baseUrl ='';
            let date = new Date();
            // let createDate = moment(date,'YYYYDDMM');
            axios.post(baseUrl,{
                title :'',
                contents :'',
                writer:'',
                create_date :'',
                updated_date :''
            });
            console.log('글을 등록하였습니다.')
        }
        return(
            
                <div className="post_add_box">
                    <div className="post_add_header">
                        <div className="post_menu_bar">
                            <div className="post_add_ico_cancel"><a className="cancel" onClick={closePop}>X</a></div>
                            <div className="menu_title">{this.props.postHeader}</div>
                            <div className="post_add_confirm_btn" onClick={addPost}>{this.props.postConfirmBtn}</div>
                        </div>
                    </div>
                    <div className="post_add_body">
                    
                        <div className="post_title_input">
                            <div className="post_title">{this.props.postTitle}</div>
                            <div className="post_input_txt"><input type="text"></input></div>
                        </div>
                        
                        
                    
                        <div className="post_contents">
                            <div className="post_txtarea"><textarea placeholder="내용을 입력하세요."></textarea></div>
                        </div>
                    </div>
                </div>
            
        );
    }
}
// const Pop = styled.div`
//     display:none;
// `
export default BoardAddPop;
