import React from "react";
import { Link } from "react-router-dom";
import axios from "axios";
function BoardMain() {
  let defaultSettings = {
    title: "게시판",
  };

  const getPostList = (e) => {
    let url = "post/1";
    let boardId = e.currentTarget.value;

    axios.get(url, {}).then((res) => {
      console.log(res);
    });
  };
  return (
    //box ( header - contents - footer )
    <div className="board_box">
      <div className="board_header">
        <Link to="/">
          <div className="board_m_prev_btn">
            <img src={require("../images/prev_btn.png")}></img>
          </div>
        </Link>
        <div className="board_m_txt">{defaultSettings.title}</div>
        <Link to="/Board/BoardAddPop">
          <div className="btn_add_post">등록</div>
        </Link>
      </div>
      <div className="board_contents">
        <table>
          <tbody>
            <tr>
              <td>
                <div onClick={getPostList}>sinsekai</div>
              </td>
            </tr>
          </tbody>
        </table>
      </div>
    </div>
  );
}
export default BoardMain;
