import { useState } from "react";
function SignUp() {
  const confirmPassword = () => {};
  const [inputs, setInputs] = useState({
    firstnm: "",
    lastnm: "",
    email: "",
    id: "",
    password: "",
    confirmpw: "",
  });
  const { firstnm, lastnm, email, id, password, confirmpw } = inputs;
  const onChange = (e) => {
    const { value } = e.target;
    setInputs({
      ...inputs,
      [firstnm]: value,
      [lastnm]: value,
      [email]: value,
      [id]: value,
      [password]: value,
      [confirmpw]: value,
    });
  };

  return (
    <div className="signup_parent_box">
      <div className="signup_box">
        <div className="signup_box_tw">
          <div className="signup_title">계정 만들기</div>
          <div className="signup_input">
            <form>
              <div className="signup_name">
                <input type="text" placeholder="성" />

                <input type="text" placeholder="이름" />
              </div>
              <div className="signup_email">
                <input type="email" placeholder="이메일" />
              </div>
              <div className="signup_id">
                <input type="text" placeholder="아이디" />
              </div>
              <div className="signup_password">
                <input type="password" placeholder="패스워드" />
              </div>
              <div className="signup_confirm_pw">
                <input type="password" placeholder="패스워드 확인" />
              </div>

              <div className="signup_btn">
                <input type="submit" value="만들기" />
              </div>
            </form>
          </div>
        </div>
      </div>
      {/* <div className="already_account">이미 계정이 있으신가요?</div> */}
    </div>
  );
}
export default SignUp;
