import React, { useState } from 'react'
import './Calendar.css';

function MainRender({currentMonth,currentMonthList,detailTextValue}){
    
    const selectedDate = (e) =>{
        // let selectedDate 
        let selectedDay = (e.target.textContent) > 9 ? (e.target.textContent) : '0'+ (e.target.textContent);
        let selectedDateValue =  currentMonth + "." + selectedDay;
        let selectedDate = new Date(selectedDateValue);
        let year = selectedDate.getFullYear();
        let month = (selectedDate.getMonth()+1) > 9 ? (selectedDate.getMonth()+1) : '0'+(selectedDate.getMonth()+1);
        let day  =(selectedDate.getDate()) >9 ? (selectedDate.getDate()) : '0'+(selectedDate.getDate())
        let showSelectedValue = year +"년 " + month +"월 " + day +"일";

        detailTextValue.current.innerHTML =showSelectedValue ;
    }
    const CalendarList = () =>{
        let calendarWeek = Math.ceil(currentMonthList.length/7);
        console.log({currentMonthList});
        let calListTr = "";
        let itemTd = "";
        return(
            ()=>{
                for(let j=1; j<=currentMonthList.length; j++){
                    for (let i=1; i<= calendarWeek; i++){
                        calListTr = "cal_list_item" + i;
                            itemTd = "item_" + j;  
                            for(let k=1; k<=7; k++){
                                console.log(i + "번째 주" + k + "일");
                                let checkDay = new Date(currentMonth+'.'+j);
                                
                                    <tr className={calListTr}>
                                        {checkDay.getDay() === 0 &&
                                        
                                            (<td className="item_0" onClick={selectedDate} data-dayno={checkDay.getDate()}></td>)
                                        }
                                        {checkDay.getDay() === 1 &&
                                        
                                            (<td className="item_1" onClick={selectedDate} data-dayno={checkDay.getDate()}></td>)
                                        }
                                        {checkDay.getDay() === 2 &&
                                        
                                            (<td className="item_2" onClick={selectedDate} data-dayno={checkDay.getDate()}></td>)
                                        }
                                        {checkDay.getDay() === 3 &&
                                        
                                            (<td className="item_3" onClick={selectedDate} data-dayno={checkDay.getDate()}></td>)
                                        }
                                        {checkDay.getDay() === 4 &&
                                        
                                            (<td className="item_4" onClick={selectedDate} data-dayno={checkDay.getDate()}></td>)
                                        }
                                        {checkDay.getDay() === 5 &&
                                        
                                            (<td className="item_5" onClick={selectedDate} data-dayno={checkDay.getDate()}></td>)
                                        }
                                        {checkDay.getDay() === 6 &&
                                        
                                            (<td className="item_6" onClick={selectedDate} data-dayno={checkDay.getDate()}></td>)
                                        }
                                        {/* <td className="item_1" onClick={selectedDate}></td>
                                        <td className="item_2" onClick={selectedDate}></td>
                                        <td className="item_3" onClick={selectedDate}></td>
                                        <td className="item_4" onClick={selectedDate}></td>
                                        <td className="item_5" onClick={selectedDate}></td>
                                        <td className="item_6" onClick={selectedDate}></td> */}
                                    </tr>
                                
                            }
                        }
                    }
                }
            
        )()
               
            // currentMonthList.map(function(a){
            //     console.log(a);
            //     let classNm = 'item_'+a;
            //     return(
            //     <tr className="cal_list_item">
            //     <td className={classNm} onClick={selectedDate}>{a}</td>
            //     {/* <td className="item_1"></td>
            //     <td className="item_2"></td>
            //     <td className="item_3"></td>
            //     <td className="item_4"></td>
            //     <td className="item_5"></td>
            //     <td className="item_6"></td> */}
            // </tr>
            // )
            // })
        
    }
    return(
        <table className="cal_list" data-calendar-date={currentMonth} data-month-list={currentMonthList}>
          <tbody>
              <tr className="cal_head">
                
                <th>일</th>
                <th>월</th>
                <th>화</th>
                <th>수</th>
                <th>목</th>
                <th>금</th>
                <th>토</th>
              </tr>
              <CalendarList />
          </tbody>
        </table>
    )
}
export default MainRender;
