import React from 'react'
import './Calendar.css';

function today(){
  var currentDate = new Date();
  var today = currentDate.getFullYear() +"."+ (currentDate.getMonth()+1) + "."+ currentDate.getDay();
  console.log("오늘의 날짜는 ", today);
  return today;
}

class CalendarComponent extends React.Component{
    render(){
        return(
              <tr className="cal_list_item">
                <button onClick={today}>day</button>
                <td></td>
                <td></td>
                <td></td>
                <td></td>
                <td></td>
                <td></td>
                <td></td>
              </tr>
        );
    }
}
export default CalendarComponent;
