import React from 'react'
import './Calendar.css';


class CalendarComponent extends React.Component{

    static defaultProps ={
      default : '',
      currentMonth : ''
    }
    render(){
        

        const getWeekOfMonth = (date) =>{
          let DayOfMonth = date.getDate();
          let first = new Date(date.getFullYear() + '/' + (date.getMonth() +1) +'/01');
          let monthFirstDay = first.getDay();

          return Math.ceil((DayOfMonth + monthFirstDay)/7);
        }

        //월의 날짜 리스트를 가져옵니다.
        const makeDateOfMonthList =()=>{
          //현재 날짜가 
          let currentDate = this.props.currentMonth !='' ? new Date(this.props.currentMonth) : new Date();
          // let currentDate = new Date();
          let lastDayOfMonth = new Date(currentDate.getFullYear(), currentDate.getMonth()+1 , 0);
          let monthList = [];
          //월의 마지막날을 가져와서 반복 문을 돌립니다.
          for (let i =1; i<=lastDayOfMonth.getDate(); i++){
              monthList.push(i);
          }
          console.log('state 넘어오는지 확인',this.props.currentMonth);
          console.log(monthList);
          console.log(monthList.length);
          getWeekOfMonth()
          return monthList;
        }
        
        return(
          <table className="cal_list">
          <tbody>
              <tr className="cal_head">
                <th className={this.props.currentMonth} onClick={makeDateOfMonthList}>일</th>
                <th>월</th>
                <th>화</th>
                <th>수</th>
                <th>목</th>
                <th>금</th>
                <th>토</th>
              </tr>
              <tr className="cal_list_item_1">
                <td></td>
                <td></td>
                <td></td>
                <td></td>
                <td></td>
                <td></td>
                <td></td>
              </tr>
              <tr className="cal_list_item_2">
                <td></td>
                <td></td>
                <td></td>
                <td></td>
                <td></td>
                <td></td>
                <td></td>
              </tr>
              <tr className="cal_list_item_3">
                <td></td>
                <td></td>
                <td></td>
                <td></td>
                <td></td>
                <td></td>
                <td></td>
              </tr>
              <tr className="cal_list_item_4">
                <td></td>
                <td></td>
                <td></td>
                <td></td>
                <td></td>
                <td></td>
                <td></td>
              </tr>
              <tr className="cal_list_item_5">
                <td></td>
                <td></td>
                <td></td>
                <td></td>
                <td></td>
                <td></td>
                <td></td>
              </tr>
          </tbody>
        </table>
        );
    }
}
export default CalendarComponent;
