import React, { useEffect, useState, useRef } from "react";
import "./Calendar.css";
import CalendarList from "./CalendarList_bak";
import { Link } from "react-router-dom";
var defaultSettings = {
  prevbtn: "<",
  nextbtn: ">",
};

const Calendar = () => {
  //오늘 날짜 가져오기
  const today = () => {
    let currentDate = new Date();
    let year = currentDate.getFullYear();
    let month =
      currentDate.getMonth() + 1 > 9
        ? currentDate.getMonth() + 1
        : "0" + (currentDate.getMonth() + 1);
    let day =
      currentDate.getDate() > 9
        ? currentDate.getDate()
        : "0" + currentDate.getDate();
    let today = year + "." + month + "." + day;

    console.log("오늘 날짜:", today);

    return today;
  };

  //현재 월 날짜 리스트를 가져옵니다.
  const makeDateOfMonthList = () => {
    //현재 날짜가
    let currentDate = current != "" ? new Date(current) : new Date();
    let lastDayOfMonth = new Date(
      currentDate.getFullYear(),
      currentDate.getMonth() + 1,
      0
    );
    let monthList = [];
    //월의 마지막날을 가져와서 반복 문을 돌립니다.
    for (let i = 1; i <= lastDayOfMonth.getDate(); i++) {
      monthList.push(i);
    }
    return monthList;
  };

  //현재 월을 가져옵니다.
  const currentMonth = () => {
    let currentMonth = new Date();
    let year = currentMonth.getFullYear();
    let month =
      currentMonth.getMonth() + 1 > 9
        ? currentMonth.getMonth() + 1
        : "0" + (currentMonth.getMonth() + 1);
    let resultDate = year + "." + month;

    console.log("현재 월", resultDate);
    return resultDate;
  };

  //다음달
  const nextMon = () => {
    let next = new Date(current);
    next.setMonth(next.getMonth() + 1);
    let nextYear = next.getFullYear();
    let nextMonth =
      next.getMonth() + 1 > 9
        ? next.getMonth() + 1
        : "0" + (next.getMonth() + 1);
    let result = nextYear + "." + nextMonth;
    setCurrent(result);
  };

  //지난달
  const preMon = () => {
    let prev = new Date(current);
    prev.setMonth(prev.getMonth() - 1);
    let prevYear = prev.getFullYear();
    let prevMonth =
      prev.getMonth() + 1 > 9
        ? prev.getMonth() + 1
        : "0" + (prev.getMonth() + 1);
    let result = prevYear + "." + prevMonth;
    setCurrent(result);
  };

  //선택 일자에 있는 날짜
  const getDetailSchedule = (e) => {
    let days = e.currentTarget.textContent;
    let scheduleDate = current + "." + days;

    //여기에 axios 호출 해서 데이터를 가져올수 있는 부분 구현

    console.log(scheduleDate);
  };
  const [current, setCurrent] = useState(currentMonth);
  const [currentMonthList, setCurrentMonthList] = useState(makeDateOfMonthList);
  const [selectedDate, setSelectedDate] = useState("");
  const detailText = useRef();

  const CalendarTr = () => {
    return <tr className="cal_list_item"></tr>;
  };
  //캘린더 날짜를 그려줍니다.
  const CalendarListData = () => {
    let calendarWeek = Math.ceil(currentMonthList.length / 7);
    let calendar = [];

    let sun = [];
    let mon = [];
    let tue = [];
    let wed = [];
    let tur = [];
    let fri = [];
    let sat = [];

    for (let j = 1; j <= currentMonthList.length; j++) {
      let dates = currentMonth + "." + j;
      let newDates = new Date(dates);

      if (newDates.getDay() === 0) {
        sun.push(newDates.getDate());
      } else if (newDates.getDay() === 1) {
        mon.push(newDates.getDate());
      } else if (newDates.getDay() === 2) {
        tue.push(newDates.getDate());
      } else if (newDates.getDay() === 3) {
        wed.push(newDates.getDate());
      } else if (newDates.getDay() === 4) {
        tur.push(newDates.getDate());
      } else if (newDates.getDay() === 5) {
        fri.push(newDates.getDate());
      } else if (newDates.getDay() === 6) {
        sat.push(newDates.getDate());
      }
    }
    calendar.push(sun);
    calendar.push(mon);
    calendar.push(tue);
    calendar.push(wed);
    calendar.push(tur);
    calendar.push(fri);
    calendar.push(sat);
    console.log(calendar);
  };

  return (
    <div>
      <div className="calendar_box">
        <div className="cal_header">
          <div className="menu_bar">
            <Link to="/">
              <div className="btn_home">{defaultSettings.prevbtn}</div>
            </Link>
            <div className="menu_title">일정 목록</div>
          </div>
          <div className="cal_title">
            <div className="prev_month_btn" onClick={preMon}>
              {defaultSettings.prevbtn}
            </div>
            <div className="current_month">{current}</div>
            <div className="next_month_btn" onClick={nextMon}>
              {defaultSettings.nextbtn}
            </div>
          </div>
        </div>
        <div className="cal_body">
          <CalendarList currentMonth={current} detailTextValue={detailText} />
        </div>
        <div className="cal_detail">
          <div className="cal_detail_title">
            <div className="selectedDate" ref={detailText}>
              {selectedDate}
            </div>
          </div>
          <div className="cal_detail_contents"></div>
        </div>
        <Link to="/Calendar/CalendarAddPop">
          <div className="addCalendar">+</div>
        </Link>
      </div>
    </div>
  );
};

export default Calendar;
