import { useState } from "react";
import styled from "styled-components";
function FindAccount() {
  const [findPassword, setFindPassword] = useState(false); //비밀번호
  const [titleLabel, steTitleLabel] = useState("아이디 찾기");
  //비밀번호 찾을시 아이디 입력란
  const FindPassword = () => {
    return (
      <div className="account_id_box">
        <div className="account_id_txt">아이디</div>
        <div className="account_id_input">
          <input type="text"></input>
        </div>
      </div>
    );
  };
  //비밀번호 찾을시 상태 값 변경
  const changeStatusPw = () => {
    setFindPassword(true);
    steTitleLabel("비밀번호 찾기");
  };
  //아이디만 찾을시 상태 값 변경
  const changeStatusId = () => {
    setFindPassword(false);
    steTitleLabel("아이디 찾기");
  };
  return (
    <div className="find_account_box">
      <Title>{titleLabel}</Title>
      {findPassword === true && <FindPassword />}
      <div className="account_email_box">
        <div className="account_email_txt">이메일</div>
        <div className="account_email_input">
          <input type="email"></input>
        </div>
      </div>
      <div className="find_btn_box">
        <div className="find_password_box">
          <div className="find_password_btn">
            <Password onClick={changeStatusPw}>비밀번호 찾기</Password>
          </div>
        </div>
        <div className="find_id_box">
          <div className="find_id_btn">
            <button className="find_id" onClick={changeStatusId}>
              아이디 찾기
            </button>
          </div>
        </div>
      </div>
    </div>
  );
}
//타이틀
const Title = styled.div`
  display: flex;
  font-size: 30px;
  font-weight: 500;
  color: gray;
`;
//비밀번호 버튼
const Password = styled.button`
  display: flex;
  font-size: 12px;
  color: white;
  background-color: skyblue;
  border-radius: 4px;
`;
//아이디 버튼

export default FindAccount;
