import React from 'react';
import {useState, userRef} from 'react';
import {Link} from 'react-router-dom';
import './Calendar.css';



function CalendarAddPop(){
        let  defaultSettings  = {
            calTitle :'일정등록',
            calConfirmBtn : '확인',
            calScheduleTitle :'일정명',
            startDateTitle : '시작',
            endDateTitle :'종료',
            attendenceTitle :'참석자' ,
            display :'false'
        }   
        const [inputs ,setInputs]= useState({
            scheduleTitle :'',
            startDate :'',
            startTime :'',
            endDate :'',
            endTime :'',
            attendence :'',
            contents :''

        });
        const {scheduleTitle,startDate,startTime,endDate,endTime,attendence,contents} = inputs;
        const onChage = (e) =>{
            const {value} = e.target;
            setInputs({
                ...inputs,
                [scheduleTitle]:value,
                [startDate]:value,
                [startTime] :value,
                [endDate] :value,
                [endTime] :value,
                [attendence] :value,
                [contents] :value
            })
            console.log(scheduleTitle)
        
        }
        // const validate = (e) =>{
        //     const {value, name} = e.target;
            
        //     setInputs({
        //         ...inputs,
        //         [scheduelTitle] : value ,

        //     })
        // }
        return(
                <div className="calendar_add_box">
                    <div className="cal_add_header">
                    <div className="menu_bar">
                        <Link to="/Calendar">
                            <div className="cal_add_ico_cancel"><a className="cancel">X</a></div>
                        </Link>
                        <div className="menu_title">{defaultSettings.calTitle}</div>
                        <div className="cal_add_confirm_btn">{defaultSettings.calConfirmBtn}</div>
                    </div>
                    </div>
                    <div className="cal_add_body">
                    
                        <div className="schedule_name">
                            <div className="sch_title">{defaultSettings.calScheduleTitle}</div>
                            <div className="sch_input_txt"><input name="scheduleTitle" type="text" onChange={onChage} value={scheduleTitle}></input></div>
                        </div>
                        <div className="shcedule_start">
                            <div className="sch_st_title">{defaultSettings.startDateTitle}</div>
                            <div className="start_date"><input name="startDate" type="date" onChange={onChage}></input></div>
                            <div className="start_time"><input name="startTime"type="time" onChange={onChage}></input></div>
                        </div>
                        <div className="shcedule_end">
                            <div className="sch_ed_title">{defaultSettings.endDateTitle}</div>
                            <div className="end_date"><input name="endDate"type="date" onChange={onChage}></input></div>
                            <div className="end_time"><input name="endTime"type="time" onChange={onChage}></input></div>
                        </div>
                        <div className="shcedule_attendence">
                            <div className="sch_atd_title">{defaultSettings.attendenceTitle}</div>
                            <div className="attendence_name"><input name="attendence"type="text" onChange={onChage}></input></div>
                        </div>
                        <div className="shcedule_contents">
                            <div className="shcedule_txtarea"><textarea name="contents"placeholder="내용을 입력하세요." onChange={onChage} ></textarea></div>
                        </div>
                    </div>
                </div>
        )
    }

export default CalendarAddPop;
