import React, { useState, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { today } from "../utils/open-sf-utils";
import "./Calendar.css";

function MainRender({ currentMonth, detailTextValue }) {
  const [current, setCurrent] = useState(currentMonth);
  //날짜 클릭시  디테일 일정을 보여줍니다.
  const selectedDate = (e) => {
    // let selectedDate
    let selectedDay =
      e.target.textContent > 9
        ? e.target.textContent
        : "0" + e.target.textContent;

    let selectedDateValue = currentMonth + "." + selectedDay;

    let selectedDate = new Date(selectedDateValue);

    let year = selectedDate.getFullYear();
    let month =
      selectedDate.getMonth() + 1 > 9
        ? selectedDate.getMonth() + 1
        : "0" + (selectedDate.getMonth() + 1);

    let day =
      selectedDate.getDate() > 9
        ? selectedDate.getDate()
        : "0" + selectedDate.getDate();

    let showSelectedValue = year + "년 " + month + "월 " + day + "일";

    detailTextValue.current.innerHTML = showSelectedValue;
  };

  //현재 월 날짜 리스트를 가져옵니다.
  const makeDateOfMonthList = () => {
    //현재 날짜가
    let currentDate = currentMonth != "" ? new Date(currentMonth) : new Date();
    let lastDayOfMonth = new Date(
      currentDate.getFullYear(),
      currentDate.getMonth() + 1,
      0
    );
    let monthList = [];
    //월의 마지막날을 가져와서 반복 문을 돌립니다.
    for (let i = 1; i <= lastDayOfMonth.getDate(); i++) {
      monthList.push(i);
    }

    return monthList;
  };

  const [currentMonthList, setCurrentMonthList] = useState(makeDateOfMonthList);
  //캘린더 TR을 생성합니다.

  return (
    <table
      className="cal_list"
      data-calendar-date={currentMonth}
      data-month-list={currentMonthList}
    >
      <tbody>
        <tr className="cal_head">
          <th>일</th>
          <th>월</th>
          <th>화</th>
          <th>수</th>
          <th>목</th>
          <th>금</th>
          <th>토</th>
        </tr>
        <tr className="cal_list_item">
          <td className="item_0"></td>
          <td className="item_1"></td>
          <td className="item_2"></td>
          <td className="item_3"></td>
          <td className="item_4"></td>
          <td className="item_5"></td>
          <td className="item_6"></td>
        </tr>
        <tr className="cal_list_item">
          <td className="item_0"></td>
          <td className="item_1"></td>
          <td className="item_2"></td>
          <td className="item_3"></td>
          <td className="item_4"></td>
          <td className="item_5"></td>
          <td className="item_6"></td>
        </tr>
        <tr className="cal_list_item">
          <td className="item_0"></td>
          <td className="item_1"></td>
          <td className="item_2"></td>
          <td className="item_3"></td>
          <td className="item_4"></td>
          <td className="item_5"></td>
          <td className="item_6"></td>
        </tr>
        <tr className="cal_list_item">
          <td className="item_0"></td>
          <td className="item_1"></td>
          <td className="item_2"></td>
          <td className="item_3"></td>
          <td className="item_4"></td>
          <td className="item_5"></td>
          <td className="item_6"></td>
        </tr>
        <tr className="cal_list_item">
          <td className="item_0"></td>
          <td className="item_1"></td>
          <td className="item_2"></td>
          <td className="item_3"></td>
          <td className="item_4"></td>
          <td className="item_5"></td>
          <td className="item_6"></td>
        </tr>
      </tbody>
    </table>
  );
}
export default MainRender;
