import React from "react";
import { useEffect } from "react";
import { Link, useNavigate } from "react-router-dom";

function PostView({ getpostId }) {
  const navigate = useNavigate();
  useEffect(() => {
    if (
      sessionStorage.getItem("user_account") === "" ||
      sessionStorage.getItem("user_account") === undefined ||
      sessionStorage.getItem("user_account") === null
    ) {
      navigate("/login");
    }
  });
  return (
    <div className="postview_box">
      <div className="postview_header">
        <div className="postview_menu">
          <div className="back_btn">뒤로</div>
          <div className="board_title">게시글 제목</div>
          <div className="board_edit">편집</div>
        </div>
      </div>
      <div className="post_detail_contents">내용</div>
    </div>
  );
}

export default PostView;
