import { useNavigate } from "react-router-dom";
import { useEffect, useState } from "react";
import axios from "axios";

function MainHome() {
  //로그인  리다이렉트
  const navigate = useNavigate();

  const [userInfo, setUserInfo] = useState([]);

  //프로필 설정 화면(팝업으로 변경 할 예정임)
  const settingProfile = () => {
    navigate("/user/update");
  };
  //로그아웃
  const logOut = () => {
    sessionOut();
    navigate("/login");
  };
  //캘린더 화면
  const calendar = () => {
    navigate("/Calendar");
  };
  //게시판 화면
  const board = () => {
    navigate("/Board");
  };

  const sessionOut = () => {
    axios({
      url: "/logout",
      method: "POST",
    });
  };
  //세션에 User-account가 남아 있지 않다면 로그인 화면으로 이동
  useEffect(() => {
    let url = "/userinfo";
    const fetchData = async () => {
      const result = await axios.get(url);
      result.data.password = "";
      setUserInfo(result);
      //console.log(result);
      if (
        result.data.userAccount === "" ||
        result.data.userAccount === undefined ||
        result.length === 0
      ) {
        navigate("/login");
      }
    };
    fetchData();
  }, []);

  return (
    <div className="home_box">
      <div className="home_header">
        <div className="profile_box">
          <div className="profile_pic" onClick={settingProfile}></div>

          <div className="profile_txt_box">
            <div className="profile_name">
              {userInfo.length === 0 ? "" : userInfo.data.userName}
            </div>
            <div className="profile_account">
              {userInfo.length === 0 ? "" : userInfo.data.userAccount}
            </div>
            {userInfo.length !== 0 && (
              <div className="profile_logout" onClick={logOut}>
                로그아웃
              </div>
            )}
          </div>
        </div>

        <div className="home_body">
          <div className="home_body_box">
            <table className="tbl_home_body">
              <thead>
                <tr>
                  <td>
                    <div onClick={calendar}>
                      <img src={require("../images/icn_calendar.png")}></img>
                      <div className="home_li_txt">캘린더</div>
                    </div>
                  </td>
                  <td>
                    <div onClick={board}>
                      <img src={require("../images/icn_board.png")}></img>
                      <div className="home_li_txt">게시판</div>
                    </div>
                  </td>
                </tr>
              </thead>
            </table>
          </div>
        </div>
        <div className="home_footer">
          <div className="home_footer_box">2022 @ ioss.co.kr | tykim</div>
        </div>
      </div>
    </div>
  );
}
export default MainHome;
