// import logo from './logo.svg';
import './App.css';
// import  './components/Component.css';
// import  './login/Login.css';
import './calendar/Calendar.css';
import './home/Home.css';
import './board/Board.css';
import './board/PostList';
import {useState} from 'react';
import {Link} from 'react-router-dom';
import PostList from './board/PostList';
import CalendarDetailList from './components/CalendarDetailList';
import CalendarComponent from './calendar/CalendarList';
import Search from './components/Search';
var defaultSettings = {
  title : '게시판',
  prevbtn: '<',
  nextbtn :'>'
}
function App() {

  const today =() =>{
    let currentDate = new Date();
    let year = currentDate.getFullYear();
    let month = (currentDate.getMonth()+1) > 9 ? (currentDate.getMonth()+1) : '0'+(currentDate.getMonth()+1);
    let day  =(currentDate.getDate()) >9 ? (currentDate.getDate()) : '0'+(currentDate.getDate())
    let today = year +'.'+ month + '.' + day;

    console.log ('오늘 날짜:' , today);

    return today;
  }
  const [current, setCurrent] = useState(today);
  const nextMon = () =>{
    let next = new Date (current);
    console.log(next);
    console.log({current});
    // setCurrent()
  }

  return (
    <div className="calendar_box">
    <div className="cal_header">
      <div className="menu_bar">
          <div className="ico_home"></div>
          <div className="menu_title">일정 목록</div>
      </div>
      <div className="cal_title">
        <div className="prev_month_btn">{defaultSettings.prevbtn}</div>
        <div className="current_month">{current}</div>
        <div className="next_month_btn" onClick={nextMon}>{defaultSettings.nextbtn}</div>
      </div>
    </div>
    <div className="cal_body">
      <table className="cal_list">
        <tbody>
          <tr className="cal_head">
            <th>일</th>
            <th>월</th>
            <th>화</th>
            <th>수</th>
            <th>목</th>
            <th>금</th>
            <th>토</th>
          </tr>
        </tbody>
      </table>
    </div>
    <div className="cal_detail">
      <div className="cal_detail_title">
        <div className="selectedDate">날짜</div>    
      </div>
        <div className="cal_detail_contents">
          <CalendarDetailList/>
          <CalendarDetailList/>
          <CalendarDetailList/>
          <CalendarDetailList/>
        </div>
    </div>
    <div className="addCalendar">
      +
    </div>


</div>

  );
}

export default App;
