import { useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
function SignUp() {
  //각 input 등의 값을 state에 저장한다.
  const [firstnm, setfirstnm] = useState("");
  const [lastnm, setlastnm] = useState("");
  const [email, setemail] = useState("");
  const [id, setid] = useState("");
  const [password, setpassword] = useState("");
  const [confirmpw, setconfirmpw] = useState("");

  //계정 만들기후 Navigate
  const navigate = useNavigate("/login");

  //input의 State를 변경하는 함수 생성
  const onFirstName = (e) => {
    setfirstnm(e.currentTarget.value);
  };
  const onLastName = (e) => {
    setlastnm(e.currentTarget.value);
  };
  const onEmail = (e) => {
    setemail(e.currentTarget.value);
  };
  const onId = (e) => {
    setid(e.currentTarget.value);
  };
  const onPassword = (e) => {
    setpassword(e.currentTarget.value);
  };
  const onConfirmpw = (e) => {
    setconfirmpw(e.currentTarget.value);
  };
  //Submit 시 Validate check 함수 (수정 필요 작동 안됨)
  const validate = () => {
    let result = true;
    if (password !== confirmpw) {
      result = false;
      return result;
    } else {
      alert("비밀번호가 같지 않습니다.");
      return result;
    }
  };
  //계정 Insert(create) 함수
  const makeUserAccount = () => {
    //axios를 이용하여 api 호출
    let validateKey = validate();

    // if (validateKey) {
    axios({
      url: "regist/account",
      headers: {
        "Content-Type": "application/json",
        // "Access-Control-Allow-Credentials": true,
      },
      withCredentials: true,
      method: "POST",
      //데이터는 추후에 변경 작업 (임시값 세팅)
      data: {
        user_name: lastnm + firstnm,
        user_account: id,
        first_nm: firstnm,
        last_nm: lastnm,
        email: email,
        password: password,
      },
    })
      //계정 생성 성공 , 실패 조건
      .then((res) => {
        console.log(res);
        if (res.data === "SUCESS") {
          console.log("계정 생성에 성공 하였습니다.");
          navigate();
        } else {
          console.log("계정 생성에 실패 하였습니다.");
        }
      })
      .catch((err) => {
        console.log(err);
      });
    // }
  };
  return (
    <div className="signup_parent_box">
      <div className="signup_box">
        <div className="signup_box_tw">
          <div className="signup_title">계정 만들기</div>
          <div className="signup_input">
            <form>
              <div className="signup_name">
                <input type="text" placeholder="성" onChange={onLastName} />
                <input type="text" placeholder="이름" onChange={onFirstName} />
              </div>
              <div className="signup_email">
                <input type="email" placeholder="이메일" onChange={onEmail} />
              </div>
              <div className="signup_id">
                <input type="text" placeholder="아이디" onChange={onId} />
              </div>
              <div className="signup_password">
                <input
                  type="password"
                  placeholder="패스워드"
                  onChange={onPassword}
                />
              </div>
              <div className="signup_confirm_pw">
                <input
                  type="password"
                  placeholder="패스워드 확인"
                  onChange={onConfirmpw}
                />
              </div>

              <div className="signup_btn">
                <input type="button" value="만들기" onClick={makeUserAccount} />
              </div>
            </form>
          </div>
        </div>
      </div>
      {/* <div className="already_account">이미 계정이 있으신가요?</div> */}
    </div>
  );
}
export default SignUp;
