  console.log('TEST next Button');
}

//다음 월 
function prevButton(){
  console.log('TEST prev Button');
}


//현재 날짜를 가져옵니다.
function getCurrentDate(){

  let currentDate = new Date();
  let month = currentDate.getMonth() +1; //Month는 +1 해줘야함
  let year = currentDate.getFullYear();
  let currentMonth = year+'.'+month;

  return currentMonth
}
var name='홍길동';
var position='대리';
function App() {

  return (
    <div className="home_box">
            <div className="home_header">
                <div className="profile_box">
                    <span className="profile_pic">pic</span>
                    <span className="profile_name">{name}</span>
                    <div className="profile_position">{position}</div>
                </div>

                <div className="home_body">
                    <div className="home_body_box">
                        <table className="tbl_body">
                            <thead>
                                <tr>
                                    <td><img src="images/icn_calendar.png"></img></td>
                                    <td><img src=""></img></td>
                                </tr>
                            </thead>
                        </table>
                    </div>
                </div>
                <div className="home_footer">
                    <div className="home_footer_box">2022 @ ioss.co.kr | tykim</div>
                </div>
            </div>
        </div>
  );
}

export default App;
