import React, { useState, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import "./Calendar.css";

function MainRender({ currentMonth, currentMonthList, detailTextValue }) {
  const [current, setCurrent] = useState(currentMonth);
  const startDay = current.clone().startOf("month").format("YYYYMMDD");
  const endDay = current.clone().startOf("month").format("YYYYMMDD");

  //날짜 클릭시  디테일 일정을 보여줍니다.
  const selectedDate = (e) => {
    // let selectedDate
    let selectedDay =
      e.target.textContent > 9
        ? e.target.textContent
        : "0" + e.target.textContent;

    let selectedDateValue = currentMonth + "." + selectedDay;

    let selectedDate = new Date(selectedDateValue);

    let year = selectedDate.getFullYear();
    let month =
      selectedDate.getMonth() + 1 > 9
        ? selectedDate.getMonth() + 1
        : "0" + (selectedDate.getMonth() + 1);

    let day =
      selectedDate.getDate() > 9
        ? selectedDate.getDate()
        : "0" + selectedDate.getDate();

    let showSelectedValue = year + "년 " + month + "월 " + day + "일";

    detailTextValue.current.innerHTML = showSelectedValue;
  };

  const DrawCalendarTr = () => {
    let weekList = [];
    let dayList = [];
    let calendarWeek = Math.ceil(currentMonthList.length / 7);
    for (var i = 1; currentMonthList <= calendarWeek; i++) {
      for (var j = 1; j <= currentMonthList; j++) {
        weekList.push(
          <tr className={i}>
            <td className={j}>{j}</td>
          </tr>
        );
      }
    }
    console.log(startDay);
    console.log(endDay);
    console.log(current);
    return weekList;
  };
  //캘린더 날짜를 그려줍니다.
  const CalendarList = () => {
    let calendarWeek = Math.ceil(currentMonthList.length / 7);
    console.log({ currentMonthList });
    let calListTr = "";
    let itemTd = "";
    let weekList = [];
    let dayList = [];

    return () => {
      for (var i = 1; i <= calendarWeek; i++) {
        //calListTr = "cal_list_item" + i;
        for (var j = 1; j <= currentMonthList.length; j++) {
          //itemTd = "item_" + j;
          for (var k = 1; k <= 7; k++) {
            let checkDay = new Date(currentMonth + "." + j);
            return (
              <tr className={calListTr}>
                <td
                  className={itemTd}
                  onClick={selectedDate}
                  data-dayno={checkDay.getDate()}
                >
                  ffsdf
                </td>
              </tr>
            );
          }
        }
      }
    };
  };
  return (
    <table
      className="cal_list"
      data-calendar-date={currentMonth}
      data-month-list={currentMonthList}
    >
      <tbody>
        <tr className="cal_head">
          <th>일</th>
          <th>월</th>
          <th>화</th>
          <th>수</th>
          <th>목</th>
          <th>금</th>
          <th>토</th>
        </tr>
        {/* <CalendarList /> */}
        <DrawCalendarTr />
      </tbody>
    </table>
  );
}
export default MainRender;
