import "./Login.css";
import { Link } from "react-router-dom";
import axios from "axios";
function Login() {
  //계정 찾기 함수
  const onLogin = () => {
    let url = "/regist/find/";
    //api 호출
    axios.post(url, {}).then((res) => {
      if (res.data === "SUCCESS") {
        console.log("계정이 존재합니다.");
      } else {
        console.log("계정이 존재 하지 않습니다.");
      }
    });
  };

  return (
    <div className="login_box">
      <div className="login_logo">
        <h1>OpenSales</h1>
      </div>
      <div className="login_form">
        <fieldset className="login_field">
          <form method="post">
            <div className="account_txt">아이디</div>
            <div className="account_input">
              <input type="text" placeholder="아이디"></input>
            </div>
            <div className="password_txt">비밀번호</div>
            <div className="password_input">
              <input type="password" placeholder="비밀번호"></input>
            </div>

            <div className="find_account_div">
              <span className="find_account">
                <Link to="/find/account">아이디 찾기</Link>
              </span>
            </div>
            <div className="submit_btn">
              <input
                className="button_submit"
                type="submit"
                value="로그인"
              ></input>
            </div>
          </form>
        </fieldset>
        <div className="sign_up">
          <div className="ment_signup">아직도 계정이 없으신가요?</div>
          <div className="link_signup">
            <Link to="/signup">SignUp</Link>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Login;
