import React from "react";
import moment from "moment";
import Header from "./Header";
import Calendar from "./Calendar";
import { Link } from "react-router-dom";
import "./Calendar.css";

export default class CalendarApp extends React.Component {
  state = {
    calendarMonth: moment(),
    today: moment(),
    // YM: calendarMonth
  };
  moveMonth = (month) => {
    this.setState({
      calendarMonth: this.state.calendarMonth.add(month, "M"),
    });
  };

  render() {
    return (
      <div className="calendar_box">
        <div className="cal_header">
          <div className="menu_bar">
            <Link to="/">
              <div className="btn_home">집</div>
            </Link>
            <div className="menu_title">일정 목록</div>
          </div>
          <div className="cal_title">
            <Header
              calendarMonth={this.state.calendarMonth.format("YYYY년 MM월")}
              moveMonth={this.moveMonth}
            />
          </div>
          <Calendar YM={this.state.calendarYM.format("YYYY-MM-DD")} />
        </div>
        <div className="cal_body">{this.Weeks(this.props.YM)}</div>
        <div className="cal_detail">
          <div className="cal_detail_title">
            <div className="selectedDate">{}</div>
          </div>
          <div className="cal_detail_contents"></div>
        </div>
        <Link to="/Calendar/CalendarAddPop">
          <div className="addCalendar">+</div>
        </Link>
      </div>
    );
  }
}
