import React from "react";
import "./Component.css";
import axios from "axios";

class CalendarDetailList extends React.Component {
  //Default Propperties settings
  static defaultProps = {
    post_picture: "",
    post_user_name: "",
    post_date: "",
    post_contents: "",
    schedules: "",
  };
  getSelectedSchedule = () => {
    let baseUrl =
      "/calendar/modify/schedule" + this.props.schedule.schedule_calendarid.id;
    axios.get(baseUrl, "/calendar/modify/schedule/").then((res) => {
      console.log(res);
      // this.props.setState({schedules: res.data});
      return res.data;
    });
  };
  render() {
    return (
      <div
        className="calendar_list_box"
        data-date-id={this.props.schedule.schedule_calendarid.id}
        data-date-writer={this.props.schedule.schedule_calendarid.scheduleMan}
        data-date-userid={this.props.schedule.scheduleUserId}
        onClick={() =>
          this.props.goview(this.props.schedule.schedule_calendarid.id)
        }
      >
        <div className="calendar_user_pic"></div>
        <div className="calendar_list_txt_box">
          <div className="calendar_user_box">
            {/* <div
              className="calendar_delete_btn"
              onClick={this.props.deleteState}
            >
              일정 삭제
            </div> */}
            <div className="calendar_user_name">
              {this.props.schedule.scheduleMan}
            </div>
            <div className="calendar_post_contents">
              {this.props.schedule.schedule_calendarid.scheduleTitle}
            </div>
          </div>
        </div>
      </div>
    );
  }
}
export default CalendarDetailList;
