import { React, useState, useEffect } from "react";
import { Link, useNavigate } from "react-router-dom";
import PostList from "./PostList";
import axios from "axios";

function BoardMain() {
  let defaultSettings = {
    title: "게시판",
  };
  const navigate = useNavigate();
  const [postList, setPostList] = useState([]);

  const getPost = () => {
    axios.get("post/1").then((res) => {
      setPostList(res.data);
    });
  };

  useEffect(getPost, []);

  const getSeePostDetail = (e) => {
    console.log("postid is ", e.currentTarget.getAttribute("data-postid"));
    navigate("/post/detail/view", {
      state: e.currentTarget.getAttribute("data-postid"),
    });
  };

  //Box 리턴
  return (
    <div className="board_box">
      <div className="board_header">
        <Link to="/">
          <div className="board_m_prev_btn">
            <img src={require("../images/b_back-30.png")} />
          </div>
        </Link>
        <div className="board_m_txt">{defaultSettings.title}</div>
        <Link to="/Board/BoardAddPop">
          <div className="btn_add_post">등록</div>
        </Link>
      </div>
      <div className="board_contents">
        <PostList postList={postList} seedetial={getSeePostDetail} />
      </div>
    </div>
  );
}
export default BoardMain;
