import React, { useEffect, useState } from "react";
import { Link, useNavigate, useLocation } from "react-router-dom";
import axios from "axios";
function PostView() {
  const navigate = useNavigate();
  const { state } = useLocation();
  useEffect(() => {});
  console.log(state);

  const [postList, setPostList] = useState([]);

  const getPost = () => {
    axios.get("post/1").then((res) => {
      setPostList(res.data);
    });
  };

  useEffect(getPost, []);
  return (
    <div className="postview_box">
      <div className="postview_header">
        <div className="postview_menu">
          <div className="back_btn">뒤로</div>
          <div className="board_title">게시글 제목</div>
          <div className="board_edit">편집</div>
        </div>
      </div>
      <div className="post_detail_contents">내용</div>
    </div>
  );
}

export default PostView;
