import React from "react";
import "./Component.css";
import { getUserInfo } from "../utils/open-sf-utils";
class CalendarDetailList extends React.Component {
  //Default Propperties settings
  static defaultProps = {
    post_picture: "",
    post_user_name: "",
    post_date: "",
    post_contents: "",
    schedules: "",
    scheduleUserProfile: "",
  };
  constructor(props) {
    super(props);
    this.state = {
      userpic: getUserInfo(this.props.schedule.scheduleUserId),
    };
  }
  // side effect
  // componentDidMount() {
  //   // this.setState({
  //   //   userProfilePic: getUserInfo(this.props.schedule.scheduleUserId),
  //   // });
  //   // console.log("before");
  //   // console.log(
  //   //   "this is getUserInfo",
  //   //   getUserInfo(this.props.schedule.scheduleUserId)
  //   // );
  //   getUserInfo(this.props.schedule.scheduleUserId);
  // }

  // componentDidUpdate() {
  //   this.setState(
  //     {
  //       userProfilePic: getUserInfo(this.props.schedule.scheduleUserId),
  //     },
  //     []
  //   );
  // }

  render() {
    let scheduleId = this.props.schedule.schedule_calendarid.id;
    let scheduleUserId = this.props.schedule.scheduleUserId;
    let writer = this.props.schedule.schedule_calendarid.scheduleMan;
    console.log(this.state.userpic);
    // let scheduleUserProfile = getUserInfo(this.props.schedule.scheduleUserId);

    // console.log("this is userProfilePic : {}", this.state.userProfilePic);
    return (
      <div
        className="calendar_list_box"
        data-date-id={scheduleId}
        data-date-writer={writer}
        data-date-userid={scheduleUserId}
        onClick={() => this.props.goDetail(scheduleId, scheduleUserId)}
      >
        <img
          className="calendar_user_pic"
          alt=""
          // src={
          //   process.env.PUBLIC_URL +
          // }
        />
        <div className="calendar_list_txt_box">
          <div className="calendar_user_box">
            <div className="calendar_user_name">
              {this.props.schedule.scheduleMan}
            </div>
            <div className="calendar_post_contents">
              {this.props.schedule.schedule_calendarid.scheduleTitle}
            </div>
          </div>
        </div>
      </div>
    );
  }
}
export default CalendarDetailList;
