import { React, useState, useEffect } from "react";
import "./Board.css";
import { today } from "../utils/open-sf-utils";
import axios from "axios";

const PostList = (postValue) => {
  let postList = JSON.parse(postValue.postValue);
  //게시글을 자세히 봅니다.
  const getPostDetail = () => {
    let baseUrl = "/post/addPost";
    axios
      .get(baseUrl, {
        // boardid: postid,
      })
      .then((res) => {
        console.log(res);
      });
  };
  const [load, setLoad] = useState(false);

  const PostBox = ({ post }) => {
    setLoad(true);

    return (
      <div className="postList_box">
        <div className="post_user_box">
          <div className="post_user_pic"></div>
          <div className="post_user_txt_box">
            <div className="post_user_name">{post.writer}</div>
            <div className="post_date">{today(post.updated_date, 1)}</div>
          </div>
        </div>

        <div className="post_contents">{post.contents}</div>
      </div>
    );
  };
  //Box리턴
  return (
    <div>
      {postList.map((post) => (
        <PostBox post={post} />
      ))}
    </div>
  );
};
export default PostList;
