import React from "react";
import "./Board.css";
import axios from "axios";

const PostList = (postValue) => {
  //게시글을 자세히 봅니다.
  const getPostDetail = () => {
    let baseUrl = "/post/addPost";

    axios
      .get(baseUrl, {
        // boardid: postid,
      })
      .then((res) => {
        console.log(res);
      });
  };

  const printPostValue = () => {
    console.log("post-List::", postValue);
  };

  //게시글 box
  const PostBox = () => {
    let postList = JSON.parse(postValue.postValue);
    return (
      <div>
        {
          <div className="postList_box">
            <div className="post_user_box">
              <div className="post_user_pic"></div>
              <div className="post_user_txt_box">
                <div className="post_user_name"></div>
                <div className="post_date"></div>
              </div>
            </div>

            <div className="post_contents"></div>
          </div>
        }
      </div>
    );
  };

  printPostValue();
  return <PostBox />;
};
export default PostList;
