import React, { useState } from 'react'
import ReactDOM from "react-dom/client";
import './Calendar.css';


class CalendarComponent extends React.Component{
    render(){

        //현재 날짜를 가져옵니다.
        const today =() =>{
          let currentDate = new Date();
          let year = currentDate.getFullYear();
          let month = (currentDate.getMonth()+1) > 9 ? (currentDate.getMonth()+1) : '0'+(currentDate.getMonth()+1);
          let day  =(currentDate.getDate()) >9 ? (currentDate.getDate()) : '0'+(currentDate.getDate())
          let today = year +'.'+ month + '.' + day;
          
          console.log ('오늘 날짜:' , today);
          makeDateOfMonthList(currentDate);
          return today;
        }
        //월의 날짜 리스트를 가져옵니다.
        const makeDateOfMonthList =(month)=>{
          let currentDate = new Date();
          let lastDayOfMonth = new Date(currentDate.getFullYear(), currentDate.getMonth()+1 , 0);
          let monthList = [];
          //월의 마지막날을 가져와서 반복 문을 돌립니다.
          for (let i =1; i<=lastDayOfMonth.getDate(); i++){
              monthList.push(i);
          }
          console.log(monthList);
          console.log(monthList.length);
          console.log(month);
        }
        return(
              <tr className="cal_list_item">
                <td></td>
                <td></td>
                <td></td>
                <td></td>
                <td></td>
                <td></td>
                <td></td>
              </tr>
        );
    }
}
export default CalendarComponent;
