import React, { useEffect, useState, useRef } from "react";
import Header from "./Header";
import "./Calendar.css";
import moment from "moment";
import { render } from "@testing-library/react";

class DateHeader extends React.Component {
  dateToArray = (dates) => {
    if (Array.isArray(dates)) {
      return dates;
    } else if (typeof dates === "string") {
      return dates.split(",");
    } else {
      return ["일", "월", "화", "수", "목", "금", "토"];
    }
  };

  mapArrayToDate = (dateArray) => {
    try {
      if (dateArray.length !== 7) {
        console.log(new Error("dates props must be had 7 date"));
        dateArray = ["일", "월", "화", "수", "목", "금", "토"];
      }

      return dateArray.map((date, index) => {
        const className = () => {
          let className = "calendar_date";
          if (index === 0) {
            return className + " date-sun";
          } else if (index === 6) {
            return className + " date-sat";
          } else {
            return className + " date-weekday";
          }
        };
        return (
          <div className={className()} key={"RCA-header-" + date}>
            {date}
          </div>
        );
      });
    } catch {
      throw new Error("날짜는 String 또는 컴포넌트가 되어야 합니다.");
    }
  };

  render() {
    return (
      <div className="calendar_week_header">
        {this.mapArrayToDate(this.dateToArray(this.props.dates))}
      </div>
    );
  }
}
//주
class Week extends React.Component {
  state = {};
  Days = (firstDayFormat, WeekIndex) => {
    const _days = [];

    for (let i = 0; i < 7; i++) {
      const Day = moment(firstDayFormat).add("d", i);
      _days.push({
        yearMonthDayFormat: Day.format("YYYY-MM-DD"),
        getDay: Day.format("D"),
        isHolyDay: false,
        WeekIndex,
      });
    }

    return _days;
  };
  mapDaysToComponents = (
    Days,
    calendarMonthYear,
    selectedDayFormat,
    fn = () => {}
  ) => {
    const thisMonth = moment(calendarMonthYear);
    return Days.map((dayInfo, i) => {
      let className = "date-weekday-label";

      if (!thisMonth.isSame(dayInfo.yearMonthDayFormat, "month")) {
        className = "date-notThisMonth";
      } else if (i === 0) {
        className = "date-sun";
      } else if (i === 6) {
        className = "date-sat";
      }

      return (
        <div
          className={"RCA-calendar-day " + className}
          key={`RCA-${dayInfo.WeekIndex}-${i}-day`}
          onClick={() => fn(dayInfo.yearMonthDayFormat)}
        >
          <label className="RCA-calendar-day-label">{dayInfo.getDay}</label>

          {/* <label className="RCA-calendar-day">{dayInfo.getDay}</label> */}
        </div>
      );
    });
  };
  render() {
    return (
      <div className="calendar_week">
        {this.mapDaysToComponents(
          this.Days(this.props.firstDayOfThisWeekformat)
        )}
      </div>
    );
  }
}

export default class Calendar extends React.Component {
  Weeks = (monthYear) => {
    const firstDayOfMonth = moment(monthYear).startOf("month");
    const firstDateOfMonth = firstDayOfMonth.get("d");

    const firstDayOfWeek = firstDayOfMonth.clone().add("d", -firstDateOfMonth);
    const _Weeks = [];

    for (let i = 0; i < 6; i++) {
      _Weeks.push(
        <Week
          key={`RCA-calendar-week-${i}`}
          firstDayOfThisWeekformat={firstDayOfWeek
            .clone()
            .add("d", i * 7)
            .format("YYYY-MM-DD")}
        />
      );
    }
    return _Weeks;
  };

  state = {
    calendarMonth: moment(),
    today: moment(),
  };
  moveMonth = (month) => {
    this.setState({
      calendarMonth: this.state.calendarMonth.add(month, "M"),
    });
  };

  render() {
    return (
      <div className="calendar_box">
        <Header
          calendarMonth={this.state.calendarMonth.format("YYYY년 MM월")}
          moveMonth={this.moveMonth}
        />
        <DateHeader />
        {this.Weeks(this.props.YM)}
        <div className="calendar_contents"></div>
      </div>
    );
  }
}
