import { Link, useNavigate } from "react-router-dom";
import { useEffect } from "react";
import { getUserInfo } from "../utils/open-sf-utils";
import moment from "moment";

function MainHome() {
  var name = "홍길동";

  //로그인  리다이렉트
  const navigate = useNavigate();

  console.log(moment());
  console.log(sessionStorage.getItem("user_account"));
  console.log(getUserInfo(sessionStorage.getItem("user_account")));

  //세션에 User-account가 남아 있지 않다면 로그인 화면으로 이동
  useEffect(() => {
    if (
      sessionStorage.getItem("user_account") === "" ||
      sessionStorage.getItem("user_account") === undefined ||
      sessionStorage.getItem("user_account") === null
    ) {
      navigate("/login");
    }
  });
  return (
    <div className="home_box">
      <div className="home_header">
        <div className="profile_box">
          <div className="profile_pic"></div>
          <div className="profile_txt_box">
            <div className="profile_name">{name}</div>
            {/* <div className="profile_position">{position}</div> */}
            {getUserInfo(sessionStorage.getItem("user_account"))}
          </div>
        </div>

        <div className="home_body">
          <div className="home_body_box">
            <table className="tbl_home_body">
              <thead>
                <tr>
                  <td>
                    <Link to="/Calendar">
                      <img src={require("../images/icn_calendar.png")}></img>
                      <div className="home_li_txt">캘린더</div>
                    </Link>
                  </td>
                  <td>
                    <Link to="/Board">
                      <img src={require("../images/icn_board.png")}></img>
                      <div className="home_li_txt">게시판</div>
                    </Link>
                  </td>
                </tr>
              </thead>
            </table>
          </div>
        </div>
        <div className="home_footer">
          <div className="home_footer_box">2022 @ ioss.co.kr | tykim</div>
        </div>
      </div>
    </div>
  );
}
export default MainHome;
