import React from 'react';
import './Calendar.css';


class CalendarAddPop extends React.Component{
    static defaultProps  = {
        calTitle :'일정등록',
        calConfirmBtn : '확인',
        calScheduleTitle :'일정명',
        startDateTitle : '시작',
        endDateTitle :'종료',
        attendenceTitle :'참석자' ,
        display: 'none'
    }   
    
    render(){
        
        // const closePop =() =>{
        //     console.log('일정등록 취소');
        //     this.setState({display : 'none'})
        // }
        return(
            <div className="calendar_add_box">
                <div className="cal_add_header">
                <div className="menu_bar">
                    <div className="cal_add_ico_cancel"><a className="cancel">X</a></div>
                    <div className="menu_title">{this.state.calTitle}</div>
                    <div className="cal_add_confirm_btn">{this.state.calConfirmBtn}</div>
                </div>
                </div>
                <div className="cal_add_body">
                
                    <div className="schedule_name">
                        <div className="sch_title">{this.props.calScheduleTitle}</div>
                        <div className="sch_input_txt"><input type="text"></input></div>
                    </div>
                    <div className="shcedule_start">
                        <div className="sch_st_title">{this.props.startDateTitle}</div>
                        <div className="start_date"><input type="date"></input></div>
                        <div className="start_time"><input type="time"></input></div>
                    </div>
                    <div className="shcedule_end">
                        <div className="sch_ed_title">{this.props.endDateTitle}</div>
                        <div className="end_date"><input type="date"></input></div>
                        <div className="end_time"><input type="time"></input></div>
                    </div>
                    <div className="shcedule_attendence">
                        <div className="sch_atd_title">{this.props.attendenceTitle}</div>
                        <div className="attendence_name"><input type="text"></input></div>
                    </div>
                    <div className="shcedule_contents">
                        <div className="shcedule_txtarea"><textarea placeholder="내용을 입력하세요."></textarea></div>
                    </div>
                </div>
            </div>
        );
    }
}
export default CalendarAddPop;
