import React from "react";
import { useState, useEffect } from "react";
import moment from "moment";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import "./Calendar.css";

function CalendarAddPop({ closeAdd }) {
  //Layout label 값
  let defaultSettings = {
    calTitle: "일정등록",
    calConfirmBtn: "확인",
    calScheduleTitle: "일정명",
    startDateTitle: "시작",
    endDateTitle: "종료",
    attendenceTitle: "참석자",
    display: "false",
  };

  //State 선언
  const [userInfo, setUserInfo] = useState([]);
  const [scheduleTitle, setScheduleTitle] = useState("");
  const [startDate, setStartDate] = useState(moment().format("YYYY-MM-DD"));
  const [startTime, setStartTime] = useState(moment().format("HH:mm"));
  const [endDate, setEndDate] = useState(moment().format("YYYY-MM-DD"));
  const [endTime, setEndTime] = useState(moment().format("HH:mm"));
  const [attendence, setAttendence] = useState("");
  const [contents, setContents] = useState("");

  const navigate = useNavigate();

  //Input값 Change 이벤트 함수
  const onChageSchedule = (e) => {
    setScheduleTitle(e.currentTarget.value);
  };
  const onChageStartDate = (e) => {
    if (startDate > endDate) {
      setStartDate(moment().format("YYYY-MM-DD"));
    } else {
      setStartDate(e.currentTarget.value);
    }
  };
  const onChageStartTime = (e) => {
    setStartTime(e.currentTarget.value);
  };
  const onChageEndDate = (e) => {
    if (endDate < startDate) {
      setEndDate(startDate);
    } else {
      setEndDate(e.currentTarget.value);
    }
  };
  const onChageEndTime = (e) => {
    setEndTime(e.currentTarget.value);
  };
  const onChageAttendence = (e) => {
    setAttendence(e.currentTarget.value);
  };
  const onChageContents = (e) => {
    setContents(e.currentTarget.value);
  };

  useEffect(() => {
    let url = "/userinfo";
    const fetchData = async () => {
      const result = await axios.get(url);
      result.data.password = "";
      setUserInfo(result);
      console.log(result);
      if (
        result.data.user_account === "" ||
        result.data.user_account === undefined ||
        result.length === 0
      ) {
        navigate("/login");
      }
    };
    fetchData();
  }, []);
  const addSchedule = () => {
    let baseUrl = "/calendar/add/schedule";

    //validate조건이 true일 경우에 axios통신을 한다.
    // if (validate() == true) {
    if (validate() === true) {
      axios
        .post(baseUrl, {
          title: scheduleTitle, //스케줄 제목
          contents: contents, //스케줄 내용
          schedule_ownder: userInfo.data.userAccount, //세션유저를 사용할예정
          userid: userInfo.data.id, //세션유저를 사용할 예정
          attendence: attendence,
          startDate: startDate,
          startTime: startTime,
          endDate: endDate,
          endTime: endTime,
        })
        .then((resp) => {
          console.log(resp);

          if (resp.data === "SUCCESS") {
            console.log("스케줄 등록에 성공 하였습니다.");
            // navigate("/Calendar");
            closeAdd();
            //아닐 경우 Fail 리턴 , FailMessgae출력
          } else if (resp.data === "FAIL") {
            console.log("스케줄 등록에 실패 하였습니다.");
          }
        })
        .catch((error) => {
          console.log(error);
        });
    }
  };

  //일정등록시 필요한 Validate 함수
  const validate = () => {
    let codeType = true;
    if (scheduleTitle === "") {
      console.log("scheduleTitle 값을 입력 해주세요.");
      codeType = false;
    } else if (startDate === "") {
      console.log("startDate 값을 입력 해주세요.");
      codeType = false;
    } else if (startTime === "") {
      console.log("startTime 값을 입력 해주세요.");
      codeType = false;
    } else if (endDate === "") {
      console.log("endDate 값을 입력 해주세요.");
      codeType = false;
    } else if (endTime === "") {
      console.log("endTime 값을 입력 해주세요.");
      codeType = false;
    } else if (attendence === "") {
      console.log("attendence 값을 입력 해주세요.");
      codeType = false;
    } else if (contents === "") {
      console.log("contents 값을 입력 해주세요.");
      codeType = false;
    }
    return codeType;
  };
  //View 리턴
  return (
    <div className="add_box">
      <div className="calendar_add_box">
        <div className="cal_add_header">
          <div className="menu_bar">
            <div className="cal_add_ico_cancel" onClick={closeAdd}>
              X
            </div>
            <div className="menu_title">{defaultSettings.calTitle}</div>
            <div className="cal_add_confirm_btn" onClick={addSchedule}>
              {defaultSettings.calConfirmBtn}
            </div>
          </div>
        </div>
        <div className="cal_add_body">
          <div className="schedule_name">
            <div className="sch_title">{defaultSettings.calScheduleTitle}</div>
            <div className="sch_input_txt">
              <input
                name="scheduleTitle"
                type="text"
                onChange={onChageSchedule}
                value={scheduleTitle}
              ></input>
            </div>
          </div>
          <div className="shcedule_start">
            <div className="sch_st_title">{defaultSettings.startDateTitle}</div>
            <div className="start_date">
              <input
                name="startDate"
                type="date"
                onChange={onChageStartDate}
                value={startDate}
              ></input>
            </div>
            <div className="start_time">
              <input
                name="startTime"
                type="time"
                onChange={onChageStartTime}
                value={startTime}
              ></input>
            </div>
          </div>
          <div className="shcedule_end">
            <div className="sch_ed_title">{defaultSettings.endDateTitle}</div>
            <div className="end_date">
              <input
                name="endDate"
                type="date"
                onChange={onChageEndDate}
                value={endDate}
                min={startDate}
              />
            </div>
            <div className="end_time">
              <input
                name="endTime"
                type="time"
                onChange={onChageEndTime}
                value={endTime}
              />
            </div>
          </div>
          <div className="shcedule_attendence">
            <div className="sch_atd_title">
              {defaultSettings.attendenceTitle}
            </div>

            <div className="attendence_name">
              <input
                name="attendence"
                type="text"
                onChange={onChageAttendence}
                value={attendence}
              ></input>
            </div>
          </div>
          <div className="shcedule_contents">
            <div className="shcedule_txtarea">
              <textarea
                name="contents"
                placeholder="내용을 입력하세요."
                onChange={onChageContents}
                value={contents}
              ></textarea>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default CalendarAddPop;
