import React from "react";
import { useEffect, useState } from "react";
import moment from "moment";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import "./Calendar.css";

function CalendarModifyPop({ detailState, closeDetail, scheduleId }) {
  //Layout label 값
  let defaultSettings = {
    calTitle: "일정수정",
    calConfirmBtn: "수정",
    calScheduleTitle: "일정명",
    startDateTitle: "시작",
    endDateTitle: "종료",
    attendenceTitle: "참석자",
    detailState: detailState,
    scheduleId: scheduleId,
  };
  const [schedules, setSchedules] = useState([]);
  //세션에 User-account가 남아 있지 않다면 로그인 화면으로 이동
  useEffect(() => {
    let url = "/calendar/modify/schedule/" + defaultSettings.scheduleId;
    const fetchData = async () => {
      const result = await axios.get(url);
      //유저 정보가 존재하지 않을경우 로그인 페이지
      if (
        result.data.scheduleId === "" ||
        result.data.scheduleId === undefined ||
        result.length === 0
      ) {
        // closeDetail();
        //존재 할 경우 state 와 비밀번호(이건 back단에서 변경할 에정)를 공백으로 변경
      } else {
        // setScheduleTitle(result.data.scheduleTitle);
        // setStartDate(result.data.startDate);
        // setStartTime(result.data.startTime);
        // setEndDate(result.data.endDate);
        // setEndTime(result.data.endTime);
        // setAttendence(result.data.attendence);
        // setContents(result.data.contents);
        setSchedules(result.data);
      }
    };
    fetchData();
  }, []);

  // Input값 State
  const [scheduleTitle, setScheduleTitle] = useState("");
  const [startDate, setStartDate] = useState(moment().format("YYYY-MM-DD"));
  const [startTime, setStartTime] = useState(moment().format("HH:mm"));
  const [endDate, setEndDate] = useState(moment().format("YYYY-MM-DD"));
  const [endTime, setEndTime] = useState(moment().format("HH:mm"));
  const [attendence, setAttendence] = useState("");
  const [contents, setContents] = useState("");

  const navigate = useNavigate();

  //Input값 Change 이벤트 함수
  const onChageSchedule = (e) => {
    setScheduleTitle(e.currentTarget.value);
  };
  const onChageStartDate = (e) => {
    if (startDate > endDate) {
      setStartDate(moment().format("YYYY-MM-DD"));
    } else {
      setStartDate(e.currentTarget.value);
    }
  };
  const onChageStartTime = (e) => {
    setStartTime(e.currentTarget.value);
  };
  const onChageEndDate = (e) => {
    if (endDate < startDate) {
      setEndDate(startDate);
    } else {
      setEndDate(e.currentTarget.value);
    }
  };
  const onChageEndTime = (e) => {
    setEndTime(e.currentTarget.value);
  };
  const onChageAttendence = (e) => {
    setAttendence(e.currentTarget.value);
  };
  const onChageContents = (e) => {
    setContents(e.currentTarget.value);
  };

  /**
   * 날짜: 2022-10-24
   * 작성자 : tykim
   * 작성내용 : 일정 리스트 클릭시 일정 수정 화면에서 클릭한 내용 불러오기
   *
   */
  const getSelectedSchedule = () => {
    let baseUrl = "/calendar/modify/schedule" + defaultSettings.scheduleId;
    axios.get(baseUrl, "/calendar/modify/schedule/").then((res) => {
      console.log(res);
      setScheduleTitle(res.data.scheduleTitle);
      setStartDate(res.data.startDate);
      setStartTime(res.data.startTime);
      setEndDate(res.data.endDate);
      setEndTime(res.data.endTime);
      setAttendence(res.data.attendence);
      setContents(res.data.contents);
    });
  };
  const modifySchedule = (e) => {
    let scheduleId = e.currentTarget.getAttribute("data-schedule-id");
    let baseUrl = "/calendar/modify/schedule" + scheduleId;

    //validate조건이 true일 경우에 axios통신을 한다.
    // if (validate() == true) {
    if (validate() === true) {
      axios
        .post(baseUrl, {
          title: scheduleTitle, //스케줄 제목
          contents: contents, //스케줄 내용
          attendence: attendence,
          startDate: startDate,
          startTime: startTime,
          endDate: endDate,
          endTime: endTime,
        })
        .then((resp) => {
          console.log(resp);

          if (resp.data === "SUCCESS") {
            console.log("스케줄 등록에 성공 하였습니다.");
            navigate("/Calendar");

            //아닐 경우 Fail 리턴 , FailMessgae출력
          } else if (resp.data === "FAIL") {
            console.log("스케줄 등록에 실패 하였습니다.");
          }
        })
        .catch((error) => {
          console.log(error);
        });
      //팝업 close
      closeDetail();
    }
  };
  //일정등록시 필요한 Validate 함수
  const validate = () => {
    let codeType = true;
    if (scheduleTitle === "") {
      console.log("scheduleTitle 값을 입력 해주세요.");
      codeType = false;
    } else if (startDate === "") {
      console.log("startDate 값을 입력 해주세요.");
      codeType = false;
    } else if (startTime === "") {
      console.log("startTime 값을 입력 해주세요.");
      codeType = false;
    } else if (endDate === "") {
      console.log("endDate 값을 입력 해주세요.");
      codeType = false;
    } else if (endTime === "") {
      console.log("endTime 값을 입력 해주세요.");
      codeType = false;
    } else if (attendence === "") {
      console.log("attendence 값을 입력 해주세요.");
      codeType = false;
    } else if (contents === "") {
      console.log("contents 값을 입력 해주세요.");
      codeType = false;
    }
    return codeType;
  };
  getSelectedSchedule();
  return (
    <div className="modify_box" data-schedules={schedules}>
      <div className="calendar_modify_box">
        <div className="cal_add_header">
          <div className="menu_bar">
            <div className="cal_add_ico_cancel" onClick={closeDetail}>
              X
            </div>
            <div className="menu_title">{defaultSettings.calTitle}</div>
            <div className="cal_add_confirm_btn" onClick={modifySchedule}>
              {defaultSettings.calConfirmBtn}
            </div>
          </div>
        </div>
        <div className="cal_add_body">
          <div className="schedule_name">
            <div className="sch_title">{defaultSettings.calScheduleTitle}</div>
            <div className="sch_input_txt">
              <input
                name="scheduleTitle"
                type="text"
                onChange={onChageSchedule}
                value={scheduleTitle}
              ></input>
            </div>
          </div>
          <div className="shcedule_start">
            <div className="sch_st_title">{defaultSettings.startDateTitle}</div>
            <div className="start_date">
              <input
                name="startDate"
                type="date"
                onChange={onChageStartDate}
                value={startDate}
              ></input>
            </div>
            <div className="start_time">
              <input
                name="startTime"
                type="time"
                onChange={onChageStartTime}
                value={startTime}
              ></input>
            </div>
          </div>
          <div className="shcedule_end">
            <div className="sch_ed_title">{defaultSettings.endDateTitle}</div>
            <div className="end_date">
              <input
                name="endDate"
                type="date"
                onChange={onChageEndDate}
                value={endDate}
                min={startDate}
              />
            </div>
            <div className="end_time">
              <input
                name="endTime"
                type="time"
                onChange={onChageEndTime}
                value={endTime}
              />
            </div>
          </div>
          <div className="shcedule_attendence">
            <div className="sch_atd_title">
              {defaultSettings.attendenceTitle}
            </div>
            <div className="attendence_name">
              <input
                name="attendence"
                type="text"
                onChange={onChageAttendence}
                value={attendence}
              ></input>
            </div>
          </div>
          <div className="shcedule_contents">
            <div className="shcedule_txtarea">
              <textarea
                name="contents"
                placeholder="내용을 입력하세요."
                onChange={onChageContents}
                value={contents}
              ></textarea>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default CalendarModifyPop;
