import { useState } from "react";
function SignUp() {
  const [firstnm, setfirstnm] = useState("");
  const [lastnm, setlastnm] = useState("");
  const [email, setemail] = useState("");
  const [id, setid] = useState("");
  const [password, setpassword] = useState("");
  const [confirmpw, setconfirmpw] = useState("");

  const onChange = (e) => {
    setfirstnm(e.target.value);
    setlastnm(e.target.value);
    setemail(e.target.value);
    setid(e.target.value);
    setpassword(e.target.value);
    setconfirmpw(e.target.value);
  };
  const onSubmit = () => {
    if (password !== confirmpw) {
      return alert("비밀번호가 같지 않습니다.");
    }
  };

  return (
    <div className="signup_parent_box">
      <div className="signup_box">
        <div className="signup_box_tw">
          <div className="signup_title">계정 만들기</div>
          <div className="signup_input">
            <form>
              <div className="signup_name">
                <input type="text" placeholder="성" onChange={onChange} />
                <input type="text" placeholder="이름" onChange={onChange} />
              </div>
              <div className="signup_email">
                <input type="email" placeholder="이메일" onChange={onChange} />
              </div>
              <div className="signup_id">
                <input type="text" placeholder="아이디" onChange={onChange} />
              </div>
              <div className="signup_password">
                <input
                  type="password"
                  placeholder="패스워드"
                  onChange={onChange}
                />
              </div>
              <div className="signup_confirm_pw">
                <input
                  type="password"
                  placeholder="패스워드 확인"
                  onChange={onChange}
                />
              </div>

              <div className="signup_btn">
                <input type="submit" value="만들기" onSubmit={onSubmit} />
              </div>
            </form>
          </div>
        </div>
      </div>
      {/* <div className="already_account">이미 계정이 있으신가요?</div> */}
    </div>
  );
}
export default SignUp;
