import { React, useState, useEffect } from "react";
import { Link } from "react-router-dom";
import PostList from "./PostList";
import axios from "axios";

function BoardMain() {
  let defaultSettings = {
    title: "게시판",
  };
  const [postList, setPostList] = useState([]);

  const getPost = () => {
    axios.get("post/1").then((res) => {
      setPostList(res.data);
    });
  };

  useEffect(getPost, []);

  //Box 리턴
  return (
    <div className="board_box">
      <div className="board_header">
        <Link to="/">
          <div className="board_m_prev_btn">
            <img src={require("../images/prev_btn.png")}></img>
          </div>
        </Link>
        <div className="board_m_txt">{defaultSettings.title}</div>
        <Link to="/Board/BoardAddPop">
          <div className="btn_add_post">등록</div>
        </Link>
      </div>
      <div className="board_contents">
        <PostList postList={postList} />
      </div>
    </div>
  );
}
export default BoardMain;
