import { useNavigate, useLocation } from "react-router-dom";
import { useEffect, useState } from "react";
import { getUserSession, getUserInfo } from "../utils/open-sf-utils";
import axios from "axios";

function MainHome() {
  //로그인  리다이렉트
  const navigate = useNavigate();
  const location = useLocation();

  const [userInfo, setUserInfo] = useState([]);

  //프로필 설정 화면(팝업으로 변경 할 예정임)
  const settingProfile = () => {
    navigate("/user/update");
  };
  //캘린더 화면
  const calendar = () => {
    navigate("/Calendar");
  };
  //게시판 화면
  const board = () => {
    navigate("/Board");
  };

  //사용자 세션을 가져와서 홈에 세팅(이걸 따로 뺄까 고민중 중복 되는 소스로 인하여 )
  const getUserSession = () => {
    let url = "/userinfo";

    axios.get(url, {}).then((res) => {
      res.data.password = "";

      if (res.data.userAccount !== "" || res.data.userAccount !== undefined) {
        setUserInfo(res);
      }
    });
  };

  useEffect(getUserSession, []);
  //세션에 User-account가 남아 있지 않다면 로그인 화면으로 이동
  // useEffect(() => {
  //   if (
  //     sessionStorage.getItem("user_account") === "" ||
  //     sessionStorage.getItem("user_account") === undefined ||
  //     sessionStorage.getItem("user_account") === null
  //   ) {
  //     navigate("/login");
  //   }
  // });
  console.log(location);

  return (
    <div>
      {userInfo !== [] && (
        <div className="home_box">
          <div className="home_header">
            <div className="profile_box">
              <div className="profile_pic" onClick={settingProfile}></div>

              <div className="profile_txt_box">
                <div className="profile_name">{userInfo.data.userName}</div>
                <div className="profile_account">
                  {userInfo.data.userAccount}
                </div>
                {/* <div className="profile_position">{position}</div> */}
              </div>
            </div>

            <div className="home_body">
              <div className="home_body_box">
                <table className="tbl_home_body">
                  <thead>
                    <tr>
                      <td>
                        <div onClick={calendar}>
                          <img
                            src={require("../images/icn_calendar.png")}
                          ></img>
                          <div className="home_li_txt">캘린더</div>
                        </div>
                      </td>
                      <td>
                        <div onClick={board}>
                          <img src={require("../images/icn_board.png")}></img>
                          <div className="home_li_txt">게시판</div>
                        </div>
                      </td>
                    </tr>
                  </thead>
                </table>
              </div>
            </div>
            <div className="home_footer">
              <div className="home_footer_box">2022 @ ioss.co.kr | tykim</div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}
export default MainHome;
