import { React, useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import PostList from "./PostList";
import axios from "axios";
import BoardHeader from "../board/BoardHeader";
import BoardAddPop from "./BoardAddPop";

function BoardMain() {
  let defaultSettings = {
    title: "게시판",
  };
  //Navigate , postList , addPopState 선언
  const navigate = useNavigate();
  const [postList, setPostList] = useState([]);
  const [addPopState, setAddPopState] = useState(false);
  const [lastIntersectingImage, setLastIntersectingImage] = (useState < HTMLDivElement) | (null > null);
  const [page, setPage] = useState(0);

  //스크롤 pagination 함수
  const handleScroll = () => {
    const scrollHeight = document.documentElement.scrollHeight;
    const scrollTop = document.documentElement.scrollTop;
    const clientHeight = document.documentElement.clientHeight;

    console.log("스크롤 이벤트 발생");
    console.log("스크롤 높이", scrollHeight);
    console.log("사용자 높이", clientHeight);

    if (scrollTop + clientHeight >= scrollHeight) {
      console.log("페이지 끝에 스크롤이 닿았음");
      setPage((prev) => prev + 1);
    }
  };
  //observer
  const onIntersect = (entries, observer) => {
    entries.forEach((entry) => {
      if (entry.isIntersecting) {
        //뷰포트에 마지막 이미지가 들어오고, page값에 1을 더하여 새 fetch 요청을 보내게됨 (useEffect의 dependency배열에 page가 있음)
        setPage((prev) => prev + 1);
        // 현재 타겟을 unobserve한다.
        observer.unobserve(entry.target);
      }
    });
  };
  //게시글 가져오는 함수 (페이징 적용한 메소드)
  const getPostdata = async () => {
    console.log("fetching 함수 호출됨");
    try {
      await axios.get(`chatter/post?page=${page}&size=5`).then((res) => {
        console.log(res);
        setPostList(res.data.content);
      });
    } catch {
      console.error("fetching error");
    }
  };

  //게시글 가져오기 (BoardId는 추후에 제거할 예정)
  // const getPost = () => {
  //   //axios.get("post/1").then((res) => {
  //   axios.get("chatter/post").then((res) => {
  //     //setPostList(res.data);
  //     setPostList(res.data.content);
  //   });
  // };

  //useEffect(getPostdata, []);
  useEffect(() => {
    console.log("page ? ", page);
    getPostdata();
  }, [page]);

  useEffect(() => {
    //   window.addEventListener("scroll", handleScroll);
    //   return () => {
    //     window.removeEventListener("scroll", handleScroll);
    //   };
    // }, []);
    let observer = new IntersectionObserver();
    if (lastIntersectingImage) {
      observer = new IntersectionObserver(onIntersect, { threshold: 0.5 });
      //observer 생성 시 observe할 target 요소는 불러온 이미지의 마지막아이템(randomImageList 배열의 마지막 아이템)으로 지정
      observer.observe(lastIntersectingImage);
    }
    return () => observer && observer.disconnect();
  }, [lastIntersectingImage]);

  //게시글 자세히 보기
  const getSeePostDetail = (e) => {
    console.log("postid is ", e.currentTarget.getAttribute("data-postid"));
    navigate("/post/detail/view", {
      state: e.currentTarget.getAttribute("data-postid"),
    });
  };
  //이전으로 돌아가기
  const goBack = () => {
    navigate("/home");
  };

  //게시글 등록 팝업 open
  const addPost = () => {
    setAddPopState(true);
  };
  //게시글 등록 팝업 close
  const closePost = () => {
    setAddPopState(false);
  };

  return (
    <div>
      {addPopState === true && <BoardAddPop leftFunc={closePost} rightFunc={closePost} />}
      <div className="board_box">
        {addPopState === false && (
          <BoardHeader leftFunc={goBack} rightFunc={addPost} title={defaultSettings.title} option={true} className={"board_header"} />
        )}
        <div className="board_contents">
          <PostList
            postList={postList}
            seedetial={getSeePostDetail}
            /** 게시글 상세보기시 goBack기능을 넣으려고 하는것 같음? */
            goBack={goBack}
          />
        </div>
      </div>
    </div>
  );
}
export default BoardMain;
