import { React, useState, useEffect } from "react";
import { Link } from "react-router-dom";
import PostList from "./PostList";
import axios from "axios";

function BoardMain() {
  let defaultSettings = {
    title: "게시판",
  };
  const [postList, setPostList] = useState("");
  const [load, setLoad] = useState(false);

  // useEffect(() => {
  //   axios.get("post/1").then((res) => {
  //     console.log(res);
  //   });
  // });

  const [inputData, setInputData] = useState([
    {
      idx: "",
      title: "",
      content: "",
      writer: "",
      write_date: "",
    },
  ]);

  useEffect(async () => {
    try {
      const res = await axios.get("post/1");

      const _inputData = await res.data.map((rowData) => ({
        idx: rowData.idx,
        title: rowData.title,
        content: rowData.content,
        writer: rowData.writer,
        write_date: rowData.write_date,
      }));
    } catch (e) {
      console.error(e.message);
    }
  }, []);
  //Box 리턴
  return (
    <div className="board_box">
      <div className="board_header">
        <Link to="/">
          <div className="board_m_prev_btn">
            <img src={require("../images/prev_btn.png")}></img>
          </div>
        </Link>
        <div className="board_m_txt">{defaultSettings.title}</div>
        <Link to="/Board/BoardAddPop">
          <div className="btn_add_post">등록</div>
        </Link>
      </div>
      <div className="board_contents">
        <PostList postValue={postList} />
      </div>
    </div>
  );
}
export default BoardMain;
