import React, { useState } from "react";
import { today } from "../utils/open-sf-utils";
function PostBox({ postList, seedetail }) {
  return (
    <div className="postList_box" data-postid={postList.id} onClick={seedetail}>
      <div className="post_user_box">
        <div className="post_user_pic"></div>
        <div className="post_user_txt_box">
          <div className="post_user_name">{postList.writer}</div>
          <div className="post_title">{postList.title}</div>
          <div className="post_date">{today(postList.updated_date, 1)}</div>
        </div>
      </div>

      {/* <div className="post_contents">{postList.contents}</div> */}
    </div>
  );
}
export default PostBox;
