import React , {useEffect,useState} from "react";
import './Calendar.css';
import CalendarComponent from "./CalendarList";
import CalendarDetailList from "../components/CalendarDetailList";
import CalendarAddPop from "./CalendarAddPop";
var defaultSettings = {
    prevbtn : '<',
    nextbtn : '>'
}

const Calendar = ({history}) =>{
   
    //오늘 날짜 가져오기
    const today =() =>{
      let currentDate = new Date();
      let year = currentDate.getFullYear();
      let month = (currentDate.getMonth()+1) > 9 ? (currentDate.getMonth()+1) : '0'+(currentDate.getMonth()+1);
      let day  =(currentDate.getDate()) >9 ? (currentDate.getDate()) : '0'+(currentDate.getDate())
      let today = year +'.'+ month + '.' + day;
      
      console.log ('오늘 날짜:' , today);

      return today;
    }
    //현재 월을 가져옵니다.
    const currentMonth =() =>{
      let currentMonth = new Date();
      let year = currentMonth.getFullYear();
      let month = (currentMonth.getMonth()+1) > 9 ? (currentMonth.getMonth()+1) : '0'+(currentMonth.getMonth()+1);
      let resultDate = year +'.'+ month ;
      
      console.log ('현재 월', resultDate);
      return resultDate;
    }

     //다음달 
     const nextMon = () =>{
      let next = new Date (current);
      next.setMonth(next.getMonth()+1);
      let nextYear = next.getFullYear();
      let nextMonth =(next.getMonth()+1) > 9 ? (next.getMonth()+1) : '0'+(next.getMonth()+1);
      let result = nextYear +'.'+nextMonth; 
      setCurrent(result);
      console.log({current});
      
    }

    //지난달
    const preMon = () =>{
      let prev = new Date (current);
      prev.setMonth(prev.getMonth()-1);
      let prevYear = prev.getFullYear();
      let prevMonth =(prev.getMonth()+1) > 9 ? (prev.getMonth()+1) : '0'+(prev.getMonth()+1);
      let result = prevYear +'.'+prevMonth; 
      setCurrent(result);
      console.log({current});
    }
  
    //선택 일자에 있는 날짜
    const getDetailSchedule = (e) => {
      let days = e.currentTarget.textContent;
      let scheduleDate = current + '.' + days;
      
      //여기에 axios 호출 해서 데이터를 가져올수 있는 부분 구현

      console.log(scheduleDate);

    }
    //일정등록 팝업 open 
    const openPop = () =>{
      console.log('일정등록 팝업' + current );
      setOpenPopup("true");
    }

      const [current , setCurrent] = useState(currentMonth);
      const [selectedDate , setSelectedDate] = useState("");
      const [calendarList,  setCalendarList] = useState();
      const [openPopup , setOpenPopup] = useState('false');
  
    return(
      <div>
        {/* {openPopup === "true" && <CalendarAddPop display={openPopup}/> } */}
        
        <div className="calendar_box">
          <div className="cal_header">
            <div className="menu_bar">
                <div className="ico_home"><a className="home"></a></div>
                <div className="menu_title">일정 목록</div>
            </div>
            <div className="cal_title">
              <div className="prev_month_btn" onClick={preMon}>{defaultSettings.prevbtn}</div>
              <div className="current_month">{current}</div>
              <div className="next_month_btn" onClick={nextMon}>{defaultSettings.nextbtn}</div>
              {/* <div className="today_btn"><button>오늘</button></div> */}
            </div>
          </div>
          <div className="cal_body">
            <CalendarComponent currentMonth={current} />
          </div>
          <div className="cal_detail">
            <div className="cal_detail_title">
              <div className="selectedDate">{selectedDate}</div>    
            </div>
              <div className="cal_detail_contents">
                
              </div>
          </div>
          <div className="addCalendar" onClick={openPop}>
            +
          </div>
      </div>
      
    </div>
    )
  }



export default Calendar;
